export default function Galeria() {

  const [itens, setItens] = useState(fotos);

  const tags = [...new Set( fotos.map((valor) => valor.tag))]

  return (
    <section className={styles.galeria}>
        <h2>Navegue pela Galeria</h2>
        <Tags/>
        <Cards itens={itens} styles={styles}/>
    </section>
  )
}
